// Extract classification from dispatcher output
const dispatcherOutput = items[0].json.output;
let classification = 'single'; // default fallback

if (dispatcherOutput && dispatcherOutput.classification) {
  classification = dispatcherOutput.classification.toLowerCase().trim();
}

// Validate classification
if (!['chat', 'single', 'bulk'].includes(classification)) {
  classification = 'single'; // fallback to single for safety
}

// Pass through all original data plus classification
return [{
  json: {
    ...items[0].json,
    classification,
    // Re-attach the original input data that we need downstream
    user_id: $('Input Validation').item.json.user_id,
    session_id: $('Input Validation').item.json.session_id,
    project_id: $('Input Validation').item.json.project_id,
    project_name: $('Input Validation').item.json.project_name,
    project_description: $('Input Validation').item.json.project_description,
    project_category: $('Input Validation').item.json.project_category,
    criteria: $('Input Validation').item.json.criteria,
    user_message: $('Input Validation').item.json.user_message,
    chat_history: $('Input Validation').item.json.chat_history,
    timestamp: $('Input Validation').item.json.timestamp
  }
}];

try {
  const aiOutput = items[0].json.output;

  // Validate AI output structure
  if (!aiOutput || typeof aiOutput.message !== 'string') {
    throw new Error('Invalid AI response: missing message');
  }

  if (!Array.isArray(aiOutput.actions)) {
    throw new Error('Invalid AI response: actions must be an array');
  }

  // Validate each action
  for (const action of aiOutput.actions) {
    if (!['create', 'update', 'delete'].includes(action.type)) {
      throw new Error(`Invalid action type: ${action.type}`);
    }

    if (!action.summary) {
      throw new Error('Each action must have a summary');
    }

    if (action.type === 'create' || action.type === 'update') {
      if (!action.criterion || !action.criterion.id || !action.criterion.name) {
        throw new Error(`${action.type} action must include criterion with id and name`);
      }
    }

    if (action.type === 'delete' && !action.criterion_id) {
      throw new Error('delete action must include criterion_id');
    }
  }

  return [{
    json: {
      success: true,
      message: aiOutput.message,
      actions: aiOutput.actions
    }
  }];

} catch (error) {
  return [{
    json: {
      success: false,
      message: '',
      actions: [],
      error: {
        code: 'AI_PROCESSING_ERROR',
        message: `AI processing failed: ${error.message}`
      }
    }
  }];
}

// Check if we already have a formatted error from Format Success Response
if (items[0].json.error && items[0].json.error.code) {
  return [{
    json: {
      success: false,
      message: '',
      actions: [],
      error: items[0].json.error
    }
  }];
}

// Otherwise, handle raw errors
const errorMessage = items[0].json.error?.message || items[0].json.message || 'An unexpected error occurred during processing';

return [{
  json: {
    success: false,
    message: '',
    actions: [],
    error: {
      code: 'INTERNAL_ERROR',
      message: errorMessage
    }
  }
}];
