try {
  const aiOutput = items[0].json.output;

  // Validate output structure
  if (!aiOutput || !Array.isArray(aiOutput.vendors)) {
    throw new Error('Invalid AI response: missing vendors array');
  }

  // Validate and clean each vendor
  const validatedVendors = [];
  for (const vendor of aiOutput.vendors) {
    if (!vendor.id || !vendor.name || !vendor.website) {
      console.log('Skipping vendor with missing required fields:', vendor.name || 'unknown');
      continue;
    }

    // Ensure features is an array
    if (!Array.isArray(vendor.features)) {
      vendor.features = [];
    }

    // Validate and clamp rating
    if (typeof vendor.rating !== 'number' || vendor.rating < 1 || vendor.rating > 5) {
      vendor.rating = 3;
    }

    // Ensure description exists
    if (!vendor.description) {
      vendor.description = '';
    }

    // Ensure pricing exists
    if (!vendor.pricing) {
      vendor.pricing = 'Contact for pricing';
    }

    // Initialize empty criteriaScores (will be populated in deep research stage)
    vendor.criteriaScores = {};

    validatedVendors.push(vendor);
  }

  if (validatedVendors.length === 0) {
    throw new Error('No valid vendors found in search results');
  }

  return [{
    json: {
      success: true,
      vendors: validatedVendors,
      search_summary: aiOutput.search_summary || `Found ${validatedVendors.length} vendors`
    }
  }];

} catch (error) {
  return [{
    json: {
      success: false,
      vendors: [],
      error: {
        code: 'AI_PROCESSING_ERROR',
        message: `Vendor search failed: ${error.message}`
      }
    }
  }];
}

// Check if we already have a formatted error from Format Success Response
if (items[0].json.error && items[0].json.error.code) {
  return [{
    json: {
      success: false,
      vendors: [],
      error: items[0].json.error
    }
  }];
}

// Otherwise, handle raw errors
const errorMessage = items[0].json.error?.message || items[0].json.message || 'An unexpected error occurred during vendor search';

return [{
  json: {
    success: false,
    vendors: [],
    error: {
      code: 'INTERNAL_ERROR',
      message: errorMessage
    }
  }
}];
