const body = items[0].json.body || {};

const user_id = body.user_id;
const session_id = body.session_id;
const project_id = body.project_id;
const project_name = body.project_name || '';
const project_description = body.project_description || '';
const project_category = body.project_category || '';
const vendor = body.vendor;
const criterion = body.criterion;
const timestamp = body.timestamp;

// Validation
const errors = [];

if (!user_id || user_id.trim() === '') {
  errors.push('user_id is required');
}

if (!session_id || session_id.trim() === '') {
  errors.push('session_id is required');
}

if (!project_id || project_id.trim() === '') {
  errors.push('project_id is required');
}

if (!project_name || project_name.trim() === '') {
  errors.push('project_name is required');
}

if (project_description.trim().length < 10) {
  errors.push('project_description must be at least 10 characters');
}

if (!project_category || project_category.trim() === '') {
  errors.push('project_category is required');
}

if (!vendor || typeof vendor !== 'object') {
  errors.push('vendor object is required');
} else {
  if (!vendor.id) errors.push('vendor.id is required');
  if (!vendor.name) errors.push('vendor.name is required');
  if (!vendor.website) errors.push('vendor.website is required');
}

if (!criterion || typeof criterion !== 'object') {
  errors.push('criterion object is required');
} else {
  if (!criterion.id) errors.push('criterion.id is required');
  if (!criterion.name) errors.push('criterion.name is required');
  if (!criterion.importance) errors.push('criterion.importance is required');
}

if (!timestamp) {
  errors.push('timestamp is required');
}

if (errors.length > 0) {
  return [{
    json: {
      validation_error: true,
      vendor_id: vendor?.id || 'unknown',
      criterion_id: criterion?.id || 'unknown',
      error_code: 'INVALID_INPUT',
      error_message: errors.join(', ')
    }
  }];
}

// Pass through validated data
return [{
  json: {
    validation_error: false,
    user_id,
    session_id,
    project_id,
    project_name: project_name.trim(),
    project_description: project_description.trim(),
    project_category: project_category.trim(),
    vendor,
    criterion,
    timestamp
  }
}];

