{
  "type": "object",
  "properties": {
    "vendor_id": { 
      "type": "string", 
      "description": "Vendor UUID - must exactly match input vendor.id" 
    },
    "criterion_id": { 
      "type": "string", 
      "description": "Criterion UUID - must exactly match input criterion.id" 
    },
    "evidence_strength": { 
      "type": "string", 
      "enum": ["yes", "unknown", "no"],
      "description": "Classification of evidence strength (yes = feature present, unknown = unclear/not found, no = explicitly absent)" 
    },
    "evidence_url": { 
      "type": "string", 
      "description": "Best URL supporting the finding (empty if no/unknown)" 
    },
    "evidence_description": { 
      "type": "string", 
      "description": "Brief description of what was found" 
    },
    "vendor_site_evidence": { 
      "type": "string", 
      "description": "URL from vendor's official site (empty if none)" 
    },
    "third_party_evidence": { 
      "type": "string", 
      "description": "URL from third-party source (empty if none)" 
    },
    "research_notes": { 
      "type": "string", 
      "description": "Summary of search process and findings" 
    },
    "search_count": { 
      "type": "number", 
      "description": "Number of Perplexity searches used (1-5)" 
    }
  },
  "required": ["vendor_id", "criterion_id", "evidence_strength", "evidence_url", "evidence_description", "search_count"]
}

parameters.jsCode:
try {
  const aiOutput = items[0].json.output;

  // Validate output structure
  if (!aiOutput || !aiOutput.vendor_id || !aiOutput.criterion_id) {
    throw new Error('Invalid AI response: missing vendor_id or criterion_id');
  }

  // Validate evidence_strength
  const validStrengths = ['yes', 'unknown', 'no'];
  if (!validStrengths.includes(aiOutput.evidence_strength)) {
    throw new Error(`Invalid evidence_strength: ${aiOutput.evidence_strength}. Must be one of: yes, unknown, no`);
  }

  // Validate that 'yes' has evidence URLs
  if (aiOutput.evidence_strength === 'yes' && 
      (!aiOutput.evidence_url || aiOutput.evidence_url.trim() === '')) {
    console.log(`Warning: 'yes' classification but no evidence URL provided`);
  }

  // Ensure all required fields have defaults
  const result = {
    vendor_id: aiOutput.vendor_id,
    criterion_id: aiOutput.criterion_id,
    evidence_strength: aiOutput.evidence_strength,
    evidence_url: aiOutput.evidence_url || '',
    evidence_description: aiOutput.evidence_description || '',
    vendor_site_evidence: aiOutput.vendor_site_evidence || '',
    third_party_evidence: aiOutput.third_party_evidence || '',
    research_notes: aiOutput.research_notes || '',
    search_count: typeof aiOutput.search_count === 'number' ? 
      Math.max(1, Math.min(5, aiOutput.search_count)) : 1
  };

  return [{
    json: {
      success: true,
      result: result,
      timestamp: new Date().toISOString()
    }
  }];

} catch (error) {
  // Try to get vendor_id and criterion_id from the original input
  let vendorId = 'unknown';
  let criterionId = 'unknown';
  try {
    const inputData = $('Input Validation').first().json;
    vendorId = inputData.vendor?.id || 'unknown';
    criterionId = inputData.criterion?.id || 'unknown';
  } catch (e) {
    // Ignore
  }

  return [{
    json: {
      success: false,
      vendor_id: vendorId,
      criterion_id: criterionId,
      error: {
        code: 'AI_PROCESSING_ERROR',
        message: `Stage 1 research failed: ${error.message}`
      }
    }
  }];
}

// Check if we already have a formatted error from Format Success Response
if (items[0].json.error && items[0].json.error.code) {
  return [{
    json: {
      success: false,
      vendor_id: items[0].json.vendor_id || 'unknown',
      criterion_id: items[0].json.criterion_id || 'unknown',
      error: items[0].json.error
    }
  }];
}

// Otherwise, handle raw errors from AI agent
const errorMessage = items[0].json.error?.message || items[0].json.message || 'An unexpected error occurred during Stage 1 research';

// Try to get vendor_id and criterion_id from the original input
let vendorId = 'unknown';
let criterionId = 'unknown';
try {
  const inputData = $('Input Validation').first().json;
  vendorId = inputData.vendor?.id || 'unknown';
  criterionId = inputData.criterion?.id || 'unknown';
} catch (e) {
  // Ignore
}

return [{
  json: {
    success: false,
    vendor_id: vendorId,
    criterion_id: criterionId,
    error: {
      code: 'INTERNAL_ERROR',
      message: errorMessage
    }
  }
}];
